pragma solidity ^0.8.2;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';

contract TokenLP1 is ERC20{
    constructor () ERC20("ElonDoge", "ELD") {
        _mint(address(this), 1000000000000000000000);
    }
    
    function faucet(address _token,uint _amount) public{
        this.transfer(_token,_amount);
    }
}
